import React from 'react';
import { withRouter } from "react-router-dom";
import Details from './Details'
import '../css/Booking.css';
import 'bootstrap/dist/css/bootstrap.min.css';

class Booking extends React.Component {
  constructor(props) {
    super(props);
    props.booking.start_date = new Date(props.booking.start_date);
    this.state = {
      showDetails: false,
      detailsLoaded: false,
      detailsLoading: false,
      details: null
    }
    this.buttonHandler = this.buttonHandler.bind(this)
    this.loadDetails = this.loadDetails.bind(this)
  }

  buttonHandler = () => {
    this.setState(prevState => ({
      showDetails: !prevState.showDetails
    }))
    this.loadDetails();
  }

  loadDetails() {
    if (this.state.detailsLoaded) {
      return;
    }
    this.setState({
      detailsLoading: true
    });
    //console.log(this.props.booking.type)
    fetch(`http://localhost:3004/${this.props.booking.type}/${this.props.booking.item_id}`)
      .then(response => response.json())
      .then(data => this.setState({ details: data }))
      .then(() => this.setState({ detailsLoading: false, detailsLoaded: true }));
  }

  render() {
    const { booking } = this.props

    // const myButton = (
    //   <button onClick={this.buttonHandler}>XD</button>
    // )

    const preDetails = (
      this.state.detailsLoading
        ? <div style={{ margin: "1%" }}><div className="spinner-border text-dark"></div></div>
        : <Details data={this.state.details} itemType={booking.type} />
    )

    return (
      <div className="card cardxd bg-light text-dark">
        <div className="card-header cardClickable" onClick={this.buttonHandler}>
          <div className="row">
            <div className="col-md-6">
              <div className="row">
                <div className="col-md-3 text-left">
                  {booking.id}
                </div>
                <div className="col-md-3 text-left">
                  {booking.owner}
                </div>
                <div className="col-md-3 text-left">
                  {booking.username}
                </div>
                <div className="col-md-3 text-left">
                  {booking.itemId}
                </div>
              </div>
            </div>
            <div className="col-md-6">
              <div className="row">
                <div className="col-md-3 text-left">
                  {booking.itemType}
                </div>
                <div className="col-md-3 text-left">
                  {booking.details}
                </div>
                <div className="col-md-3 text-left">
                  {booking.active.toString()}
                </div>
                <div className="col-md-3 text-left">
                  {`${booking.startDateTime.getDate()} 
                    ${booking.startDateTime.getMonth() + 1} ${booking.startDateTime.getFullYear()}`}
                </div>
              </div>
            </div>
          </div>
        </div>
        {this.state.showDetails && preDetails}
      </div>
    )
  }
}

export default withRouter(Booking)

